from typing import List

class Solution:
    res = False
    def rec(self, board, i, j, remainToMatch):
        if self.res:
            return
        if i < 0 or j < 0:
            return
        if i >= len(board) or j >= len(board[0]):
            return


        if board[i][j] != remainToMatch[0]:
            return
        else:
            next = remainToMatch[1:]
            if next == "":
                self.res = True
                return
            tmp = board[i][j]
            board[i][j] = ""
            self.rec(board, i + 1, j, next)
            self.rec(board, i - 1, j, next)
            self.rec(board, i, j - 1, next)
            self.rec(board, i, j + 1, next)
            board[i][j] = tmp
            if self.res:
                return True

    def exist(self, board: List[List[str]], word: str) -> bool:
        i = 0
        while i < len(board):
            j = 0
            while j < len(board[i]):
                if board[i][j] == word[0]:
                    self.rec(board, i, j, word)
                    if self.res:
                        return True
                j = j +1
            i = i + 1
        return self.res


